CREATE TABLE IF NOT EXISTS print_logs ( id INT NOT NULL AUTO_INCREMENT, order_detail_id INT NOT NULL, printedQty DECIMAL(12,3) NOT NULL, employeeId INT NOT NULL, logDate DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP, PRIMARY KEY (id), KEY idx_print_logs_order_detail (order_detail_id), KEY idx_print_logs_employee (employeeId), CONSTRAINT fk_print_logs_order_detail FOREIGN KEY (order_detail_id) REFERENCES order_details (Id) ON DELETE CASCADE, CONSTRAINT fk_print_logs_employee FOREIGN KEY (employeeId) REFERENCES employee (id) ON DELETE RESTRICT ) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;
